import React, {useState, useEffect, useRef} from 'react';

const CreateOrder = () => {

    // const [ServicesData, setServicesData] = useState();
    let ServicesData
    const [Order, setOrder] = useState({});
    const [Sum, setSum] = useState({});
    const inputRef = useRef(null)

    useEffect(() => {
        fetch("/api/servicesdata", {
            method: "GET",
        })
        .then((response) => response.json())
        .then((data) => {
            ServicesData = data;
        })
        .catch((error) => console.log(error));
    }, []);

     function changeCount(event) {
        event.preventDefault();
        inputRef.current.focus();
        setOrder(Order => ({...Order, [event.target.id]: event.target.value}))
        setSum(Sum => ({...Sum,[event.target.id]: event.target.value * event.target.name}))
     }

    function OrderList() {
        if (ServicesData) {
            console.log("Render List")

            const List = ServicesData.map((superType) => 
            <div key={superType["super_type_id"]}>
                <div>{superType["super_type"]}</div>

                <div>
                    {superType["data"].map((type) =>
                        <div key={type["type_id"]}>
                            <div>{type["type"]}</div>

                            <div>{type["data"].map((service) =>
                                <div key={service["id"]}>
                                    <div>{service["name"]}</div> 
                                    <div>цена: {service["cost"]}</div>
                                    <input id={service["id"]} name={service["cost"]} type="text" value={Order[service["id"]]} onChange={changeCount} placeholder="0" ref={inputRef}></input>
                                </div>
                            )}</div>
                        </div>

                    )}
                </div>
            </div> 
            )
            return List
        }

    }

    function SendOrder() {
        console.log(Order)
    }


    function CalcSum() {
        let summa = 0
        for (const [key, value] of Object.entries(Sum)) {
            summa += value
        }

        return <div>{summa}</div>
    }



  return (
    <div>
        <OrderList/>
        <CalcSum/>
        <div onClick={SendOrder}>Сделать заказ</div>
    </div>
  );
};
export default CreateOrder;
