import React, { useState, useEffect } from "react";
import { getOrders } from "../../api/orders";
import classes from "./Orders.module.css";

const Orders = () => {
  const [orders, setOrders] = useState();

  const [SlideInfo, setSlideInfo] = useState("Waiting");
  const [SlideStyle, setSlideStyle] = useState("");
  const [WaitingStyle, setWaitingStyle] = useState("solid 1px black");
  const [WorkStyle, setSWorkStyle] = useState("");
  const [ComletedStyle, setComletedStyle] = useState("");

  useEffect(() => {
    const data = getOrders(localStorage.getItem("token"));
    data.then((result) => {
      setOrders(result);
      console.log(result);
    });
  }, []);

  if (orders) {
    return (
      <div className={classes.wrap}>
        <div className={classes.header}>
          <div onClick={SlideToWaiting}>
            <div style={{ border: WaitingStyle }}>Ждет подтверждения</div>
          </div>
          <div onClick={SlideToWork}>
            <div style={{ border: WorkStyle }}>В работе</div>
          </div>
          <div onClick={SlideToComleted}>
            <div style={{ border: ComletedStyle }}>Окончено</div>
          </div>
        </div>
        <div className={classes.wrapwrapcontent}>
          <div className={classes.wrapcontent}>
            <div className={classes.waiting}>
              {orders[0].map((order) => (
                <div>
                  <div>
                    {order.date}, {order.in_work}, {order.is_completed},{" "}
                    {order.note}, {order.cell}
                  </div>
                  <div>
                    {order.data.map((orderInfo) => (
                      <div>
                        {orderInfo.super_type}/{orderInfo.type}/
                        {orderInfo.service} - {orderInfo.count}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
            <div className={classes.work}>
              {orders[1].map((order) => (
                <div>
                  <div>
                    {order.date}, {order.in_work}, {order.is_completed},{" "}
                    {order.note}, {order.cell}
                  </div>
                  <div>
                    {order.data.map((orderInfo) => (
                      <div>
                        {orderInfo.super_type}/{orderInfo.type}/
                        {orderInfo.service} - {orderInfo.count}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
            <div className={classes.completed}>
              {orders[2].map((order) => (
                <div>
                  <div>
                    {order.date}, {order.in_work}, {order.is_completed},{" "}
                    {order.note}, {order.cell}
                  </div>
                  <div>
                    {order.data.map((orderInfo) => (
                      <div>
                        {orderInfo.super_type}/{orderInfo.type}/
                        {orderInfo.service} - {orderInfo.count}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }
};
export default Orders;
