import React from 'react';
import AuthForm from '../auth/AuthForm';
import CreateOrder from '../createorder/CreateOrder';
import Info from '../info/Info';

const Main = () => {

  let isUser = false

  if (localStorage.getItem("token")) {
    isUser = true
  }

  function UserGreeting(){
    return (
      <div>
      <Info/>
      <CreateOrder/>
      </div>
  )
  }

  function GuestGreeting() {
    return (
      <div>
      <Info/>
      <CreateOrder/>
      <AuthForm/>
      </div>

  )
  }

  function AuthCheck() {
    if (localStorage.getItem("token")) {
      return (
        <div>
        <Info/>
        <CreateOrder/>
        </div>
    )
    } else {
      <div>
      <Info/>
      <CreateOrder/>
      <AuthForm/>
      </div>
    }
  }

  if (isUser) {
    return UserGreeting()
  }
  else {
    return GuestGreeting()
  }
};
export default Main;
