import React, {useState, useEffect, useCallback} from 'react';

const CreateOrder = () => {

    const [ServicesData, setServicesData] = useState();
    // let ServicesData
    const [Order, setOrder] = useState({});
    const [Sum, setSum] = useState({});

    useEffect(() => {
        fetch("/api/servicesdata", {
            method: "GET",
        })
        .then((response) => response.json())
        .then((data) => {
            setServicesData(data)
        })
        .catch((error) => console.log(error));
    }, []);

    //  function changeCount(event) {
    //     setOrder(Order => ({...Order, [event.target.id]: event.target.value}))

    //     setSum(Sum => ({...Sum,[event.target.id]: event.target.value * event.target.name}))
    //  }

     const changeCount = (e, id, type, superType, cost) => {
        setServicesData((ServicesData) => ServicesData.map((Super) => {
            if (Super.super_type_id === superType) { Super.data.map((Type) => {
                if (Type.type_id === type) {Type.data.map((s) => {
                    if (s.id === id) s.count = e.target.value
                    return s
                })
                }
                return type
            });
            }
            return Super
        })
        );

        setSum(Sum => ({...Sum,[id]: e.target.value * cost}))
     };

    const  OrderList = () => {
        if (ServicesData) {
            console.log("Render List")

            const List = ServicesData.map((superType) => 
            <div key={superType["super_type_id"]}>
                <div>{superType["super_type"]}</div>

                <div>
                    {superType["data"].map((type) =>
                        <div key={type["type_id"]}>
                            <div>{type["type"]}</div>

                            <div>{type["data"].map((service) =>
                                <div key={service["id"]}>
                                    <div>{service["name"]}</div> 
                                    <div>цена: {service["cost"]}</div>
                                    {/* <input
                                    id={service["id"]}
                                    name={service["cost"]}
                                    type="text"
                                    value={Order[service["id"]]}
                                    onChange={changeCount}
                                    placeholder="0">
                                    </input> */}
                                    <input
                                    type="text"
                                    value={service.count}
                                    onChange={(e) => changeCount(e, service.id, type.type_id, superType.super_type_id, service.cost)}
                                    placeholder="0">
                                    </input>
                                </div>
                            )}</div>
                        </div>

                    )}
                </div>
            </div> 
            )
            return List
        }

    }

    function SendOrder() {
        console.log(ServicesData)
    }


    function CalcSum() {
        let summa = 0
        for (const [key, value] of Object.entries(Sum)) {
            summa += value
        }

        return <div>{summa}</div>
    }



  return (
    <div>
        <OrderList/>
        <CalcSum/>
        <div onClick={SendOrder}>Сделать заказ</div>
    </div>
  );
};
export default CreateOrder;
