const PrivateRoute = () => {
  const { isAuthenticated } = useContext(AuthContext);
  const location = useLocation();

  return (
    isAuthenticated === true ?
      <Outlet />
      :
      <Navigate to="/" state={{ from: location }} replace />
  );
}

export default App;
