import React, {useState, useEffect} from 'react';

const CreateOrder = () => {

    const [ServicesData, setServicesData] = useState();
    const [Order, setOrder] = useState();
    const [Cost, setCost] = useState(0);

    useEffect(() => {
        fetch("/api/servicesdata", {
            method: "GET",
        })
        .then((response) => response.json())
        .then((data) => {
            setServicesData(data);
        })
        .catch((error) => console.log(error));
    }, []);

    function UpdateCost(delta){
        setCost(Cost + delta)
    }

    // console.log(ServicesData)

    function OrderList() {
        if (ServicesData) {
            const list = ServicesData.map((superType) => 
            <div>
                <div>{superType["super_type"]}</div>

                <div>
                    {superType["data"].map((type) =>
                        <div>
                            <div>{type["type"]}</div>

                            <div>{type["data"].map((service) => 
                                <div>
                                    <div>{service["name"]}</div> 
                                    <div>{service["cost"]}</div> 
                                    <button onClick={UpdateCost(service["cost"])}>+</button>
                                    <button onClick={UpdateCost(service["cost"])}>-</button>
                                </div>
                            )}</div>
                        </div>

                    )}
                </div>
            </div> 
            );
            return list
        }

    }

