import React, {useState} from 'react';
import { singup, login, token, confirm, reset, confirmReset, saveNewPassword } from '../../api/auth';

const AuthForm = () => {

    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [telegram, setTelegram] = useState('');
    const [code, setCode] = useState('');
    const [resetCode, setResetCode] = useState('');
    const [resetEmail, setResetEmail] = useState('');
    const [newPassword, setNewPassword] = useState('');


    const SingUp = (e) => {
        e.preventDefault()
        confirm(email);
    }

    const Login = (e) => {
      e.preventDefault() 
      const token = login(email, password);

      token.then((result) => {
        if (result !== "Not User") {
          localStorage.setItem("token", result.token)
        }
      });
    }

    const Confirm = (e) => {
      e.preventDefault()
      const token = singup(email, password, name, telegram, code);
      token.then((result) => {
        if (result !== "Error Code") {
          localStorage.setItem("token", result.token)
        }
      });
    }

    const Reset = (e) => {
      e.preventDefault()
      reset(resetEmail)
    }

    const ConfirmReset = (e) => {
      e.preventDefault()
      const resettoken = confirmReset(resetEmail, resetCode)
      resettoken.then((result) => {
        if (result !== "Error Code") {
          localStorage.setItem("resettoken", result.token)
        }
      });
    }

    const SaveNewPassword = (e) => {
      e.preventDefault()
      const token = saveNewPassword(localStorage.getItem("resettoken"), newPassword)
      token.then((result) => {
        if (result !== "Error Code") {
          localStorage.setItem("resettoken", result.token)
        }
      });
      localStorage.removeItem("resettoken")
    }

  return (
    <div>
        <form>
            <input type="text" onChange={e => setEmail(e.target.value)}/>
            <input type="text" onChange={e => setPassword(e.target.value)}/>
            <input type="text" onChange={e => setName(e.target.value)}/>
            <input type="text" onChange={e => setTelegram(e.target.value)}/>
            <button onClick={SingUp}>Отправить</button>
      </form>
      <form>
      <input type="text" onChange={e => setCode(e.target.value)}/>
        <button onClick={Confirm}>Подтвердить</button>
    </form>
      <form>
        <input type="text" onChange={e => setEmail(e.target.value)}/>
        <button onClick={Login}>Отправить</button>
    </form>
    <form>
        <input type="text" onChange={e => setResetEmail(e.target.value)}/>
        <button onClick={Reset}>Сбросить</button>
    </form>
    <form>
        <input type="text" onChange={e => setResetCode(e.target.value)}/>
        <button onClick={ConfirmReset}>Подтвердить</button>
    </form>
    <form>
        <input type="text" onChange={e => setNewPassword(e.target.value)}/>
        <button onClick={SaveNewPassword}>Сохранить</button>
    </form>

  </div>
  );
};
export default AuthForm;
