import React, {useState, useEffect} from 'react';

const CreateOrder = () => {

    const [ServicesData, setServicesData] = useState();
    // const [Order, setOrder] = useState();
    let Order ={}
    const [Cost, setCost] = useState(0);

    useEffect(() => {
        fetch("/api/servicesdata", {
            method: "GET",
        })
        .then((response) => response.json())
        .then((data) => {
            setServicesData(data);
        })
        .catch((error) => console.log(error));
    }, []);

    // console.log(ServicesData)

    function updateOrder(id, cost) {
        Order[id] += cost
        setCost(Cost + cost)
    }

    function OrderList() {
        if (ServicesData) {
            ServicesData.map((superType) => 
                superType["data"].map((type) =>
                    type["data"].map((service) =>
                        // Order.push({"super_type": superType["super_type_id"], "type": type["type_id"], "services": service["id"], "count": service["count"]})
                        Order[service["id"]] = 0

                    )
                )
            )

            const list = ServicesData.map((superType) => 
            <div>
                <div>{superType["super_type"]}</div>

                <div>
                    {superType["data"].map((type) =>
                        <div>
                            <div>{type["type"]}</div>

                            <div>{type["data"].map((service) =>
                                <div>
                                    <div>{service["name"]}</div> 
                                    <div>цена: {service["cost"]}</div>
                                    {/* <div>количество: {Order[service["id"]]}</div> */}
                                    <input type="text" value={Order[service["id"]]} onInput={e => Order[service["id"]] = e.target.value}/>
                                </div>
                            )}</div>
                        </div>

                    )}
                </div>
            </div> 
            );
            return list
        }

    }

    function SendOrder() {

    }

  return (
    <div>
        <OrderList/>
        <div>сумма: {Cost}</div>
        <button onClick={SendOrder}>Сделать заказ</button>
    </div>
  );
};
export default CreateOrder;
