import React, {useState, useEffect} from 'react';
// import {useForm} from 'react-hook-form'

const CreateOrder = () => {

    const [ServicesData, setServicesData] = useState();
    const [Order, setOrder] = useState({});
    // let Order ={}
    const [Cost, setCost] = useState("0");

    useEffect(() => {
        fetch("/api/servicesdata", {
            method: "GET",
        })
        .then((response) => response.json())
        .then((data) => {
            setServicesData(data);
        })
        .catch((error) => console.log(error));
    }, []);

    function changeText(event) {
        setCost(event.target.value);
        console.log(event.target.id)
     }

     function changeCount(event) {
        event.preventDefault();
        // if (Order[idService]) {
        //     Order[idService] = value
        // }
        // else {
        setOrder(Order => ({...Order, [event.target.id]: event.target.value}))
        // }

     }

     let List


     if (ServicesData) {
        console.log(123)
        const List = ServicesData.map((superType) => 
        <div key={superType["super_type_id"]}>
            <div>{superType["super_type"]}</div>

            <div>
                {superType["data"].map((type) =>
                    <div key={type["type_id"]}>
                        <div>{type["type"]}</div>

                        <div>{type["data"].map((service) =>
                            <div key={service["id"]}>
                                <div>{service["name"]}</div> 
                                <div>цена: {service["cost"]}</div>
                                <input id={service["id"]} type="text" value={Order[service["id"]]} onChange={changeCount} placeholder="0"></input>
                            </div>
                        )}</div>
                    </div>

                )}
            </div>
        </div> 
        );
     }



    function OrderList() {
        if (ServicesData) {
            console.log(123)
            // ServicesData.map((superType) => 
            //     superType["data"].map((type) =>
            //         type["data"].map((service) => {
            //             // Order.push({"super_type": superType["super_type_id"], "type": type["type_id"], "services": service["id"], "count": service["count"]})
            //             // Order[service["id"]] = "0"
            //             // let id = service["id"]
            //             // const updateOrder = {[id]: "0"}
            //             // setOrder(Order => ({...Order, ...updateOrder}))



            //         })
            //     ) 
            // )

            const List = ServicesData.map((superType) => 
            <div key={superType["super_type_id"]}>
                <div>{superType["super_type"]}</div>

                <div>
                    {superType["data"].map((type) =>
                        <div key={type["type_id"]}>
                            <div>{type["type"]}</div>

                            <div>{type["data"].map((service) =>
                                <div key={service["id"]}>
                                    <div>{service["name"]}</div> 
                                    <div>цена: {service["cost"]}</div>
                                    <input id={service["id"]} type="text" value={Order[service["id"]]} onChange={changeCount} placeholder="0"></input>
                                </div>
                            )}</div>
                        </div>

                    )}
                </div>
            </div> 
            );
            return List
        }

    }

    function SendOrder() {
        console.log(Order)
    }



  return (
    <form>
        <List/>
        <div>сумма: {Cost}</div>
        <input id="2" type="text" value={Cost} onChange={changeText} />
        <div onClick={SendOrder}>Сделать заказ</div>
    </form>
  );
};
export default CreateOrder;
