import React, {useState} from 'react';
import { singup, login, token } from '../../api/auth';

const AuthForm = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [telegram, setTelegram] = useState('');

    const SingUp = (e) => {
        e.preventDefault() 
        const token = singup(email, password, name, telegram);
        localStorage.setItem("token", token)
        // const tokenStorage = localStorage.getItem("token")
        // console.log(tokenStorage)
    }

    const Login = (e) => {
      e.preventDefault() 
      const token = login(email, password);
      // console.log(token)
      localStorage.setItem("token", token)
      // const tokenStorage = localStorage.getItem("token")
      // console.log(tokenStorage)

    }

    const Token = (e) => {
      e.preventDefault() 
      const formData = {"token": localStorage.getItem("token")}


    const request = new Request('/api/token', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    const response = fetch(request);

    if (response.status === 500) {
      throw new Error('Internal server error');
    }

    const data = response.json();

    console.log(data);

    }

  return (
    <div>
        <form>
            <input type="text" onChange={e => setEmail(e.target.value)}/>
            <input type="text" onChange={e => setPassword(e.target.value)}/>
            <input type="text" onChange={e => setName(e.target.value)}/>
            <input type="text" onChange={e => setTelegram(e.target.value)}/>
            <button onClick={SingUp}>Отправить</button>
      </form>
      <form>
        <input type="text" onChange={e => setEmail(e.target.value)}/>
        <input type="text" onChange={e => setPassword(e.target.value)}/>
        <button onClick={Login}>Отправить</button>
    </form>
    <form>
        <button onClick={Token}>Отправить</button>
    </form>
  </div>
  );
};
export default AuthForm;
